var test = require('tape');
var functionsHaveNames = require('functions-have-names');
var hasSymbols = require('has-symbols');

require('events/tests/legacy-compat');
var common = require('events/tests/common');

// we do this to easily wrap each file in a mocha test
// and also have browserify be able to statically analyze this file
var orig_require = require;
var require = function(file) {
    test(file, function(t) {
        // Store the tape object so tests can access it.
        t.on('end', function () { delete common.test; });
        common.test = t;

        try {
          var exp = orig_require(file);
          if (exp && exp.then) {
            exp.then(function () { t.end(); }, t.fail);
            return;
          }
        } catch (err) {
          t.fail(err);
        }
        t.end();
    });
};

require('events/tests/add-listeners.js');
require('events/tests/check-listener-leaks.js');
require('events/tests/errors.js');
require('events/tests/events-list.js');
if (typeof Promise === 'function') {
  require('events/tests/events-once.js');
} else {
  // Promise support is not available.
  test('./events-once.js', { skip: true }, function () {});
}
require('events/tests/listener-count.js');
require('events/tests/listeners-side-effects.js');
require('events/tests/listeners.js');
require('events/tests/max-listeners.js');
if (functionsHaveNames()) {
  require('events/tests/method-names.js');
} else {
  // Function.name is not supported in IE
  test('./method-names.js', { skip: true }, function () {});
}
require('events/tests/modify-in-emit.js');
require('events/tests/num-args.js');
require('events/tests/once.js');
require('events/tests/prepend.js');
require('events/tests/set-max-listeners-side-effects.js');
require('events/tests/special-event-names.js');
require('events/tests/subclass.js');
if (hasSymbols()) {
  require('events/tests/symbols.js');
} else {
  // Symbol is not available.
  test('./symbols.js', { skip: true }, function () {});
}
require('events/tests/remove-all-listeners.js');
require('events/tests/remove-listeners.js');
